import React, { useState } from "react";
import { useSelector } from "react-redux";
import { dataHubType, productsType } from "../../Types/types";
import Footer from "../Footer/Footer";
import Hero from "./Hero";
import HomeNav from "./HomeNav";
const Home = () => {
  const dataHub: dataHubType = useSelector((state: dataHubType) => state);
  let [products, setProducts] = useState(dataHub.products);
  // if user is guest user then use this state
  let [state, setState] = useState({
    cart: [],
    filter: [],
  });

  const addTocart = (index: number, x: productsType) => {
    if (dataHub.session) {
    } else {
    }
  };

  const selectionList: (key: string) => string[] = (key: string) => {
    let brands: string[] = [];
    dataHub.products.map((x: any) => {
      if (!brands.includes(x[key])) brands.push(x[key]);
    });
    return brands;
  };

  const filterHandler=(key:string,val:string)=>{
   let currentFilter:any= state.filter
   if(currentFilter[key]){
    let index=currentFilter[key].indexOf(val)
    if(index!==-1)
    currentFilter[key].splice(index,1)
    else
   currentFilter[key]=[...currentFilter[key],val]
  }
   else
   currentFilter[key]=[val]

   setState({...state,filter:{...currentFilter}})

  let filtered=dataHub.products.filter((item:any)=>{
    // products
 Object.keys(currentFilter).map(keys=>
    {     
      // brand price
  currentFilter[keys].map((values:any)=>{
 item[keys]==values
     }) 
    })
   })

console.log("=>",filtered)


  }
  console.log(dataHub);
  return (
    <>
      <HomeNav />
      <Hero />
      <div className="container-fluid my-5 pt-5">
        {/* Uncomment It */}
        <div className="col-12">
          <h1 className="mt-5">Products Page || Admin Panel Dashboard</h1>
          <p className="lead">
            E-commerce Store with all Important features and maximum controlled UI from dataSets. Also Given a dashboard{" "} , to open it <br></br>
            <code className="small">Email:</code> jai@gmail.com{" "}&{" "}
            <code className="small">Password</code> 2222.<br></br>
            There is three user role exist: <br />
            <code className="small">1.Admin</code><br></br>
            <code className="small">2.Manager</code><br></br>
            <code className="small">3.User</code><br></br>

          </p>
        </div> 
        <div className="col-12 d-flex">
          <div className="col-12 col-sm-10">
            <div className="row row-cols-1 row-cols-md-4 m-2 g-4">
              {products.map((x, index) => (
                <div className="col">
                  <div className="card h-100">
                    <img
                      style={{ height: "200px" }}
                      src={Array.isArray(x.images) ? x.images[0] : x.images}
                      className="card-img-top"
                      alt="..."
                    />
                    <div className="card-body d-flex flex-column justify-content-between">
                      <div>
                        <h5 className="card-title ">{x.title}</h5>
                        <p className="card-text">{x.description}</p>
                      </div>
                      <div className="px-3 py-2 third-bg rounded col-12 fw-bold fs-5 d-flex justify-content-between align-items-center mt-4">
                        <button
                          className="btn btn-outline-light"
                          onClick={() => addTocart(index, x)}
                        >
                          <i className="bi bi-cart-plus-fill me-2"></i>
                          Add to Cart
                        </button>
                        <span>
                          <i className="bi bi-dash-square text-white  fs-4"></i>
                          <span className="mx-2">5</span>
                          <i className="bi bi-plus-square text-white fs-4"></i>
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* filterer */}
          <div className="d-none d-sm-block col-2 second-bg rounded p-2">
            <h4 className="text-white my-2">
              <i className="bi bi-filter-left me-1"></i>Filter
            </h4>
            {/* Filter by selections */}
            <hr className="text-white" />
                  <h5 className="text-white my-2">
                    <i className='bi bi-calendar3-range me-1'></i>
                    By Price
                  </h5>
                  <ul className="list-group filter--list">
                    {['0-500','500-1000','1000-2000','2000-5000','more than 5000'].map((x) => (
                      <li className="list-group-item">
                        <input
                          className="form-check-input me-1"
                          type="checkbox"
                          value=""
                          aria-label="..."
                        />
                        {x} <i className="bi bi-currency-rupee"></i>
                      </li>
                    ))}
                  </ul>
            {[
              { title: "By Categories", key: "category", icon: "bi-check-all" },
              { title: "By Brands", key: "brand", icon: "bi-postage" },
              { title: "By Discount", key: "discountPercentage", icon: "bi-postage" },
            ].map(
              (base) => (
                <>
                  <hr className="text-white" />
                  <h5 className="text-white my-2">
                    <i className={`bi ${base.icon} me-1`}></i>
                    {base.title}
                  </h5>
                  <ul className="list-group filter--list">
                    {selectionList(base.key).map((x) => (
                      <li className="list-group-item">
                        <input
                          className="form-check-input me-1"
                          type="checkbox"
                          value=""
                          onChange={()=>filterHandler(base.key,x)}
                          aria-label="..."
                        />
                        {x}{(base.key=='discountPercentage')?' %':''}
                      </li>
                    ))}
                              <li className="list-group-item text-danger">No more choices Available</li>
                  </ul>
                </>
              )
            )}

          </div>
        </div>
      </div>
      <div className="col-12 ">
        <Footer />
      </div>
    </>
  );
};

export default Home;
