import React, { Component } from "react";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";

export default function SimpleSlider({ arr }: { arr: string[] }) {
  var settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };
  return (
    <Slider {...settings}>
      {arr.map((element) => (
        <div>
          <img
            src={element}
            style={{ maxWidth: "100px", maxHeight: "100px", marginTop: "5px" }}
            alt={element}
          />
        </div>
      ))}
    </Slider>
  );
}
