            <h5 className="text-white my-2"><i class="bi bi-microsoft"></i> By Brands</h5>
            <ul className="list-group">
              {brandsList().map((x) => (
                <li className="list-group-item">
                  <input
                    className="form-check-input me-1"
                    type="checkbox"
                    value=""
                    aria-label="..."
                  />
                 {x}
                </li>
              ))}
            </ul>
