import React, { useRef, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import usePrimaryKey from "../Components/Hooks/usePrimaryKey";
import { addUser, setSession } from "../Features/Slice";
import { dataHubType, userTypes } from "../Types/types";
import "./LoginSignup.css";
type MySetting = {
  keyGenerator: (lists: any) => string | undefined;
};
const LoginSignup = () => {
  const users: userTypes[] = useSelector((state: dataHubType) => state.users);
  const dispatch = useDispatch();
  const [login, setLogin] = useState(true);
  const { keyGenerator }: MySetting = usePrimaryKey();
  const msg = useRef<HTMLH>();
  const AuthHandler = (e: React.FormEvent<HTMLFormElement>) => {
    const email = e.currentTarget.email.value;
    const password = e.currentTarget.password.value;
    if (login) {
      let details = users.filter(
        (x) => x.email === email && x.password === password
      );
      if (details.length === 1) {
        dispatch(setSession({ ...details[0] }));
      } else {
        msg.current.innerText = "wrnong Username or Password";
      }
    } else {
      const name = e.currentTarget.username.value;
      dispatch(
        addUser({
          id: keyGenerator(users),
          name,
          role: "User",
          email,
          password,
        })
      );
    }
    e.currentTarget.reset();
  };
  console.log(users);
  return (
    <>
      <div className="row justify-content-center">
        <div className="col-lg-4 col-md-6 d-flex flex-column align-items-center justify-content-center">
          <div className="d-flex justify-content-center py-4">
            <a
              href="index.html"
              className="logo d-flex align-items-center w-auto"
            >
              <img src="assets/img/logo.png" alt="" />
              <span className="d-none d-lg-block">NiceAdmin</span>
            </a>
          </div>
          <div className="card mb-3">
            <div className="card-body">
              <div className="pt-4 pb-2">
                <h5 className="card-title text-center pb-0 fs-4">
                  {login ? "Login to Your Account" : "Create Your Account"}
                </h5>
                <p className="text-center small">
                  {login
                    ? " Enter your username & password to login"
                    : "Fill details to signUp"}
                </p>
              </div>
              <form
                className="row g-3 needs-validation"
                onSubmit={(e) => {
                  e.preventDefault();
                  AuthHandler(e);
                }}
              >
                {!login && (
                  <div className="col-12">
                    <label htmlFor="yourUsername" className="form-label">
                      Name
                    </label>
                    <div className="input-group has-validation">
                      {/* <span className="input-group-text" id="inputGroupPrepend">
                            Email
                          </span> */}
                      <input
                        type="text"
                        name="username"
                        className="form-control"
                        id="yourUsername"
                        required
                      />
                      <div className="invalid-feedback">
                        Please enter your username.
                      </div>
                    </div>
                  </div>
                )}
                <div className="col-12">
                  <label htmlFor="yourUsername" className="form-label">
                    Email
                  </label>
                  <div className="input-group has-validation">
                    {/* <span className="input-group-text" id="inputGroupPrepend">
                      Email
                    </span> */}
                    <input
                      type="email"
                      name="email"
                      className="form-control"
                      id="yourUsername"
                      required
                    />
                    <div className="invalid-feedback">
                      Please enter your username.
                    </div>
                  </div>
                </div>
                <div className="col-12">
                  <label htmlFor="yourPassword" className="form-label">
                    Password
                  </label>
                  <input
                    type="password"
                    name="password"
                    className="form-control"
                    id="yourPassword"
                    required
                  />
                  <div className="invalid-feedback">
                    Please enter your password!
                  </div>
                </div>
                <div className="col-12">
                  <div className="form-check">
                    <input
                      className="form-check-input"
                      type="checkbox"
                      name="remember"
                      value="true"
                      id="rememberMe"
                    />
                    <label className="form-check-label" htmlFor="rememberMe">
                      Remember me
                    </label>
                  </div>
                </div>
                <div className="col-12">
                  <button className="btn btn-primary w-100" type="submit">
                    {login ? "Login" : "Create Account"}
                  </button>
                </div>
                <div className="col-12">
                  <p className="small mb-0">
                    {login ? "Don't" : "Already"} have account?
                    <a href="#" onClick={() => setLogin((prev) => !prev)}>
                      {login ? "Create an account" : "Sign In?"}
                    </a>
                  </p>
                </div>
              </form>
            </div>
          </div>
          <div className="credits">
            <p ref={msg} className=""></p>
          </div>
        </div>
      </div>
    </>
  );
};

export default LoginSignup;
