import React, { Component } from "react";

export default function SimpleSlider({ arr }: { arr: string[] }) {
  var settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };
  return (
    <div>
      {arr.map((element) => (

          <img
            src={element}
            style={{ maxWidth: "100px", maxHeight: "100px", marginTop: "5px" }}
            alt={element}
          />

      ))}     </div>
  );
}
