import React from "react";
import { createSlice } from "@reduxjs/toolkit";
import { initialState } from "./database";

const MySlice = createSlice({
  name: "mySlice",
  initialState,
  reducers: {
    setAll:(state,action)=>{
    state={...action.payload}
    },
// action.payload {key:'objName' , data:{}}
    pushData:(state:any,action)=>{
state[action.payload.key].push({...action.payload.data})
    },
    // lets deleteFunc // action.payload {key:'objName' , data:{}}
    deleteFunc:(state,action)=>{
      state[action.payload.key].push({...action.payload.data})
    }
  },
});

export default MySlice.reducer
export const {setAll , pushData} =  MySlice.actions
